<?php

namespace Bpi\ApiBundle\Domain\Repository;

use Doctrine\ODM\MongoDB\DocumentRepository;

/**
 * UserRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class UserRepository extends DocumentRepository
{
    /**
     * Check if User with such internal name exitst
     *
     * @param string $internalName
     * @return bool
     */
    public function findSimilarUserByInternalName($internalName)
    {
        $query = $this->createQueryBuilder('Entity\User')
            ->field('internalUserName')
            ->equals($internalName)
        ;

        $result = $query
            ->getQuery()
            ->getSingleResult()
        ;

        return ($result) ? true : false;
    }

    /**
     * Find user by external id and agency public id
     *
     * @param string $externalId
     * @param string $agencyPublicId
     * @return array|null|object
     */
    public function findByExternalIdAgency($externalId, $agencyPublicId)
    {
        $agency = $this->dm
            ->getRepository('BpiApiBundle:Aggregate\Agency')
            ->findOneBy(array('public_id' => $agencyPublicId))
        ;
        $query = $this->createQueryBuilder('Entity\User')
            ->field('externalId')
            ->equals($externalId)
            ->field('userAgency.id')
            ->equals($agency->getId())
        ;

        $result = $query
            ->getQuery()
            ->getSingleResult()
        ;

        return $result;
    }
}
